		if(!$components)  {
			$this->error("EA-GPCom","Component Not Found",func_get_args());
			return false;
		}

		$get_page_components= array();

		foreach ($components as $key => &$component) {
			$component->data=$component->get_data();
			$get_page_components[]=$component->attributes;
