				return false;
			} 
			$result= Component::model()->findByPk($new_id);

			
			$result->data=$result->get_data();

			

			if(!$result)  {
				$this->error("EA-ACom","Component Not Found",func_get_args(),$new_component);
				return false;
			}

			//echo CJSON::encode($result);
